package de.rwth.i9.simt.im.model.eval;

import static javax.persistence.GenerationType.IDENTITY;

import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.GeneratedValue;
import javax.persistence.Id;
import javax.persistence.Table;

/**
 * Publication generated by hbm2java
 */
@Entity
@Table(name = "publication", catalog = "enwikidb")
public class Publication implements java.io.Serializable {

	private Integer id;
	private String defaultKeywords;
	private String textContent;
	private String title;

	public Publication() {
	}

	public Publication(String defaultKeywords, String textContent, String title) {
		this.defaultKeywords = defaultKeywords;
		this.textContent = textContent;
		this.title = title;
	}

	@Id
	@GeneratedValue(strategy = IDENTITY)

	@Column(name = "id", unique = true, nullable = false)
	public Integer getId() {
		return this.id;
	}

	public void setId(Integer id) {
		this.id = id;
	}

	@Column(name = "default_keywords")
	public String getDefaultKeywords() {
		return this.defaultKeywords;
	}

	public void setDefaultKeywords(String defaultKeywords) {
		this.defaultKeywords = defaultKeywords;
	}

	@Column(name = "text_content")
	public String getTextContent() {
		return this.textContent;
	}

	public void setTextContent(String textContent) {
		this.textContent = textContent;
	}

	@Column(name = "title")
	public String getTitle() {
		return this.title;
	}

	public void setTitle(String title) {
		this.title = title;
	}

}
